// DATASET: classrooms from ./datasets/classrooms
const classPrompts = {
  feClassrooms() {
    // Create an array of just the front-end classrooms. e.g.
    // [
    //   { roomLetter: 'A', program: 'FE', capacity: 32 },
    //   { roomLetter: 'C', program: 'FE', capacity: 27 },
    //   { roomLetter: 'E', program: 'FE', capacity: 22 },
    //   { roomLetter: 'G', program: 'FE', capacity: 29 }
    // ]

    /* CODE GOES HERE */
    let feRooms = classrooms.filter((classroom) => {
      return classroom.program === 'FE'
    })

    return feRooms

    // Annotation:
    // Write your annotation here as a comment
  },

  totalCapacities() {
    // Create an object where the keys are 'feCapacity' and 'beCapacity',
    // and the values are the total capacity for all classrooms in each program e.g.
    // {
    //   feCapacity: 110,
    //   beCapacity: 96
    // }

    /* CODE GOES HERE */
    //output: object with two properties

    let feCapacity = classrooms.reduce((sum, classroom) => {
      if (classroom.program === 'FE') {
        sum += classroom.capacity
      }
      return sum
    }, 0)
    // console.log(feCapacity)
    let beCapacity = classrooms.reduce((sum, classroom) => {
      if (classroom.program === 'BE') {
        sum += classroom.capacity
      }
      return sum
    }, 0)
    // console.log(beCapacity)
    return {
      feCapacity,
      beCapacity
    }

    // Annotation:
    // Write your annotation here as a comment
    // DON'T FORGET YOUR INITIAL VALUE FOR YOUR ACCUMULATOR
  },

  sortByCapacity() {
    // Return the array of classrooms sorted by their capacity (least capacity to greatest)

    /* CODE GOES HERE */
    let sortedRooms = classrooms.sort((a, b) => a.capacity - b.capacity)
    // console.log(sortedRooms)
    return sortedRooms

    // Annotation:
    // Write your annotation here as a comment
    // a - b for ascending order
    // b - a for descending order
  }
};

