// DATASET: weather from './datasets/weather

const weatherPrompts = {
  getAverageTemps() {
    // return an array of all the average temperatures. Eg:
    // [ 40, 40, 44.5, 43.5, 57, 35, 65.5, 62, 14, 46.5 ]

    /* CODE GOES HERE */
    //output: array of numbers (same length as original)
    //1. map weather array
    //2. declare avg temp variable
    //3. assign city temperature high + city temp low / 2 to avg temp
    //4. return avg temp

    let avgTemps = weather.map((city) => {
      let cityAvg = (city.temperature.high + city.temperature.low) / 2
      // console.log(cityAvg)
      return cityAvg

    });
    // console.log(avgTemps)
    return avgTemps

    // Annotation:
    // Write your annotation here as a comment
  },

  findSunnySpots() {
    // Return an array of sentences of the locations that are sunny
    // and mostly sunny. Include the location and weather type. Eg:
    // [ 'Atlanta, Georgia is sunny.',
    // 'New Orleans, Louisiana is sunny.',
    // 'Raleigh, North Carolina is mostly sunny.' ]

    /* CODE GOES HERE */

    let sunnyCities = weather.filter(({ type }) => { //destructure example//
      return type === 'sunny' || type === 'mostly sunny'
    })
    // console.log(sunnyCities)
    let sunnyStatements = sunnyCities.map((city) => {
      return `${city.location} is ${city.type}.`
    })

    return sunnyStatements
    // Annotation:
    // Write your annotation here as a comment
    //REMEMBER and/or operator here needed full expression not just
    // the second value we're looking for 
    // city.type === 'sunny' || 'mostly sunny' is incorrect
  },

  findHighestHumidity() {
    // Return the location with the highest humidity. Eg:
    // {
    //   location: 'Portland, Oregon',
    //   type: 'cloudy',
    //   humidity: 84,
    //   temperature: { high: 49, low: 38 }
    // }

    /* CODE GOES HERE */
    //output: object
    //1. store sorted array in order of humidity
    //2. return index position 0

    let sortedCities = weather.sort((a, b) => b.humidity - a.humidity)
    // console.log('sortedCities: ', sortedCities)
    let highestHumidity = sortedCities[0]
    return highestHumidity

    // Annotation:
    // Write your annotation here as a comment

  }
};

